import React from 'react'
import { Button } from 'react-bootstrap';
import './App.css'
import Subsection from './Subsection.js'

class Section extends React.Component {

	getSubsections() {
		let subsections = [];

		for (const subsection of Object.values(this.props.data.sections[0].subsections)) {
			subsections.push(
				<Subsection key={subsection.number} data={subsection}/>
			)
		}

		return subsections;
	}

	addTime() {
		var data = this.props.data;
		console.log(data);
		console.log("?");
		for (var key in data.time) {
			var time = <li> key: {data.time[key]}</li>
			console.log(time);
			document.getElementById('times').appendChild(time);
		}
	}

	render() {
		var data = this.props.data;
		// console.log(typeof(data.time));

		// section time
		// var time = "";
		// for (var key in data.time) {
		// 	time = "";
		// 	time += "<>" + key + ": ";
		// 	time += data.time[key] + "<> \n";

		// }
		// document.getElementById("times").appendChild(time);



		return (
			<div>
				<h3>
					<li>{data.number} <Button> Add Section </Button></li>
				</h3>
				<ul>
					<li> Instructor: {data.instructor} </li>
					<li> Location: {data.location} </li>
					<li id="times"> Meeting Times </li>
					<script>addTime()</script>
				</ul>
				<h3>Subsections</h3>
				{this.getSubsections()}
			</div>
		)
	}
}

export default Section;
