import React from 'react';
import './App.css';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import Sidebar from './Sidebar';
import CourseArea from './CourseArea';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      allCourses: {},
      filteredCourses: {},
      subjects: [],
      cartCourses: {}
      // cart: {{course: string}, {section: array}, {subsection: array}}
      // cart {course: {section: [subsection]}}
      // cart["PSYCH202"] = {};
      // cart["PSYCH202"]["LEC001"] = []
      // cart["PSYCH202"]["LEC001"].push("Subsection")

      // cart["PSYCH202"]["LEC001"].remove("Subsection")
      // delete cart["PSYCH202"]["LEC001"]
      // delete cart["PSYCH202"]
      // also remove parents if zero is left
    };
  }

  // have 3 parameters for all methods

  isInCart(course, section, subsection) {

    return false;
  }

  addToCart() {
    console.log("Parker is cute.");

  }

  removeFromCart() {

  }

  componentDidMount() {
    fetch('http://mysqlcs639.cs.wisc.edu:53706/api/react/classes').then(
      res => res.json()
    ).then(data => this.setState({allCourses: data, filteredCourses: data, cartCourses: cartCourses, subjects: this.getSubjects(data)}));
  }

  getSubjects(data) {
    let subjects = [];
    subjects.push("All");

    for(const course of Object.values(data)) {
      if(subjects.indexOf(course.subject) === -1)
        subjects.push(course.subject);
    }

    return subjects;
  }

  setCourses(courses) {
    this.setState({filteredCourses: courses})
  }

  setCart(courses) {
    this.setState({cartCourses: courses});
  }

  render() {
    return (
      <>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
          integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
          crossOrigin="anonymous"
        />



        <Tabs defaultActiveKey="search" style={{position: 'fixed', zIndex: 1, width: '100%', backgroundColor: 'white'}}>
          <Tab eventKey="search" title="Search" style={{paddingTop: '5vh'}}>
            <Sidebar setCourses={(courses) => this.setCourses(courses)} courses={this.state.allCourses} subjects={this.state.subjects}/>
            <div style={{marginLeft: '20vw'}}>
              <CourseArea data={this.state.filteredCourses} allData={this.state.allCourses} addToCart={this.addToCart} removeFromCart={this.removeFromCart}isInCart={this.isInCart} cartMode={false} />
          </div>
          </Tab>

          <Tab eventKey="cart" title="Cart" style={{paddingTop: '5vh'}}>
            <div style={{marginLeft: '5vw'}}>
              <CourseArea data={this.state.cartCourses} allData={this.state.allCourses} addToCart={this.addToCart} removeFromCart={this.removeFromCart} isInCart={this.isInCart} cartMode={true}/>
            </div>
          </Tab>
        </Tabs> 
      </>
    )
  }
}

export default App;
