import React from 'react';
import { Button } from 'react-bootstrap';
import './App.css';
import Section from './Section'

class Course extends React.Component {
  getSections() {
    let sections = [];

    for (const section of Object.values(this.props.data.sections)) {
      sections.push (
        <Section key={section.number} data={section} addToCart={this.props.addToCart} removeFromCart={this.props.removeFromCart} isInCart={this.props.isInCart}/>
      )
    }

    return sections;
  }

  render() {
    var data = this.props.data
    console.log(data);

    // keywords
    var length = data.keywords.length;
    var keywords = "Keywords: ";
    for (var i = 0; i < length; i++) {
      if (i == length - 1) {
        keywords += data.keywords[i]
      }
      else {
        keywords += data.keywords[i] + ", ";
      }
    }

    // requisites
    length = data.requisites.length;
    var requisites = "Requisites: ";
    if (length == 0) {
      requisites += "None";
    }
    else {
      for (i = 0; i < data.requisites.length; i++) {
        //console.log(data.requisites[i]); // array
        var one_req = "(";
        for (var j = 0; j < data.requisites[i].length; j++) {
          // console.log(data.requisites[i][j])
          one_req += data.requisites[i][j];
          if (j != data.requisites[i].length - 1) {
            one_req += " OR "
          }
        }
        one_req += ")"
        requisites += one_req;
        if (i != data.requisites.length - 1) {
          requisites += " AND "
        }
      }
    }
    
    var button = <Button onClick={this.props.addToCart}> Add Course </Button>
    if (this.props.isInCart()) {
      button = <Button variant="warning" onClick={this.props.removeFromCart}> Remove Course </Button>
    }

    return (
      <div>
        <h2>
          ({data.number}) {data.name} | {data.credits} credits  
          {button}
        </h2>
        <h3>Subject: {data.subject}</h3>
        <p>{data.description}</p>
        <p>{requisites}</p>
        <p>{keywords}</p>
        <h2>Sections:</h2>
        {this.getSections()}
      </div>
    )
  }
}

export default Course;
