import React from 'react'
import { Button } from 'react-bootstrap';
import './App.css'
import Subsection from './Subsection.js'

class Section extends React.Component {

	getSubsections() {
		let subsections = [];

		for (const subsection of Object.values(this.props.data.subsections)) {
			subsections.push(
				<Subsection key={subsection.number} data={subsection}/>
			)
		}


		return subsections;
	}

	getTimes() {
		var data = this.props.data;
		console.log(data);
		console.log("?");

		for (var key in data.time) {
			var time = <li> key: {data.time[key]}</li>
			console.log(time);
			times.push(
				<li>{key}: {time} </li>
			)
		}
	}

	render() {
		var data = this.props.data;


		return (
			<div>
				<h3>
					<li>{data.number} <Button> Add Section </Button></li>
				</h3>
				<ul>
					<li> Instructor: {data.instructor} </li>
					<li> Location: {data.location} </li>
					<li id="times"> Meeting Times </li>
					<ul>
						{this.getTimes()}
					</ul>
				</ul>
				<h4>Subsection</h4>
				{this.getSubsections()}
				<br></br>
				<br></br>
				<br></br>
			</div>
		)
	}
}

export default Section;
