class SearchAndFilter {
  searchAndFilter(courses, search, subject, minimumCredits, maximumCredits) {

    for (let i = 0; i < courses.length; i++) {
      if((!minimum || (courses[i].credits >= minimumCredits)) && !maximum || (courses[i].credits <= maximumCredits)) {
        courses_credit.push(courses[i]);
      }
      else {
        console.log("mininum is: " + minimumCredits + "maximum is: " + maximumCredits + "actual is: " + courses[i].credits)
      }
    }

    return courses_credit;
  }
}

export default SearchAndFilter;
