import React from 'react'
import { Button } from 'react-bootstrap';
import './App.css'
import Subsection from './Subsection.js'

class Section extends React.Component {

	getSubsections() {
		let subsections = [];

		for (const subsection of Object.values(this.props.data.subsections)) {
			subsections.push(
				<Subsection key={subsection.number} data={subsection} addToCart={this.props.addToCart} removeFromCart={this.props.removeFromCart} isInCart={this.props.isInCart}/>
			)
		}


		return subsections;
	}

	getTimes() {
		var data = this.props.data;

		let times = [];

		for (var key in data.time) {
			var time = data.time[key]
			times.push(
				<li key={key}>{key}: {time} </li>
			)
		}

		return times;
	}



	render() {
		var data = this.props.data;

		console.log(this.props.course);

		var button = <Button onClick={() => this.props.addToCart(this.props.course, data.number, null)}> Add Section </Button>
		if (this.props.isInCart()) {
		button = <Button variant="warning" onClick={this.props.removeFromCart}> Remove Section </Button>
		}

		return (
			<div>
				<h3>
					<li>{data.number} {button} </li>
				</h3>
				<ul>
					<li> Instructor: {data.instructor} </li>
					<li> Location: {data.location} </li>
					<li id="times"> Meeting Times </li>
					<ul>
						{this.getTimes()}
					</ul>
				</ul>
				<h4>Subsection</h4>
				{this.getSubsections()}
				<br></br>
				<br></br>
				<br></br>
			</div>
		)
	}
}

export default Section;
